package com.haldes.string;

/*
Initially, there is a JudgingRouteCircle at position (0, 0). Given a sequence of its moves, judge if this robot makes a circle, which means it moves back to the original place.

The move sequence is represented by a string. And each move is represent by a character. The valid robot moves are R (Right), L (Left), U (Up) and D (down). The output should be true or false representing whether the robot makes a circle.

Example 1:
Input: "UD"
Output: true
Example 2:
Input: "LL"
Output: false
 */


public class JudgingRouteCircle {


    public static void main(String[] args) {

        String moves = "";
        SolutionRobot sr = new SolutionRobot();
        boolean result = sr.roboSol(moves);

        System.out.println("The Result : " + result);
    }

}


class SolutionRobot {
    public boolean roboSol(String moves) {
        //int[][]cord = new int[10][2];
        int x = 0;
        int y = 0;
        char[] arr = moves.toCharArray();
        for (int i = 0; i < moves.length(); i++) {
            //System.out.println("element i :" + arr[i]);
            char move = arr[i];
            if (move == 'U') {
                y++;
                System.out.println("Moving Up. x : " + x + " y : " + y);
            } else if (move == 'D') {
                y--;
                System.out.println("Moving Down. x : " + x + " y : " + y);
            } else if (move == 'L') {
                x--;
                System.out.println("Moving Left. x : " + x + " y : " + y);
            } else if (move == 'R') {
                x++;
                System.out.println("Moving Right. x : " + x + " y : " + y);
            }
        }

        if (x == 0 && y == 0) {
            return true;
        } else {
            return false;
        }

    }
}




